function toggleCheckbox(checkboxId){const checkbox=document.getElementById(checkboxId);checkbox.checked=!checkbox.checked;document.getElementById("option_all").checked=false}function toggleRadio(){const checkboxes=document.querySelectorAll("input[type='checkbox'][name='imageType']");const radio=document.getElementById("option_all");if(radio.checked){checkboxes.forEach(checkbox=>checkbox.checked=false)}else{const anyCheckboxChecked=Array.from(checkboxes).some(checkbox=>checkbox.checked);radio.checked=!anyCheckboxChecked}}document.addEventListener("DOMContentLoaded",function(){document.getElementById("url").addEventListener("keydown",function(event){if(event.key==="Enter"){event.preventDefault()}});const buttons=document.querySelectorAll("button.option");buttons.forEach(button=>{button.addEventListener("click",function(){const checkboxId=this.getAttribute("data-checkbox-id");toggleCheckbox(checkboxId)})});const form=document.querySelector(".crawl_form");form.addEventListener("click",function(event){const target=event.target;if(target.matches(".selection")){document.getElementById("option_all").checked=false}});document.getElementById("option_all").addEventListener("click",function(){const checkboxes=document.querySelectorAll("input[type='checkbox'][name='imageType']");checkboxes.forEach(checkbox=>checkbox.checked=false)})});